import React, { useState, useEffect } from "react";
import axios from "axios";

const App = () => {
  // Create a state variable to store the API response
  const [data, setData] = useState([]);

  // Use the useEffect hook to call the API on mount
  useEffect(() => {
    // Get the backend URI from the environment variable
    const backendUri = process.env.REACT_APP_BACKEND_URI;

    // Make the API call
    axios.get("http://3.134.113.238:5000/api/data")
      .then((response) => response.data)
      .then((data) => setData(data));
  }, []);

  // Display the API response on the index HTML file
  return (
    <div>
      <h1>API Response</h1>
      <ul>
        {data.map((item, index) => (
          <li key={index}>{item.name}</li>
        ))}
      </ul>
    </div>
  );
};

export default App;
