// Create a React component to handle the API call
import React, { useState, useEffect } from "react";

const ApiCall = () => {
  // Create a state variable to store the API response
  const [data, setData] = useState([]);

  // Use the useEffect hook to call the API on mount
  useEffect(() => {
    // Get the backend URI from the environment variable
    const backendUri = process.env.REACT_APP_BACKEND_URI;

    // Make the API call
    fetch(backendUri + "/api/data")
      .then((response) => response.json())
      .then((data) => setData(data));
  }, []);

  // Print the API response in a proper format
  return (
    <div>
      <h1>API Response</h1>
      <ul>
        {data.map((item, index) => (
          <li key={index}>{item.name}</li>
        ))}
      </ul>
    </div>
  );
};

// Export the component
export default ApiCall;
